// SPDX-License-Identifier: MIT
pragma solidity 0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import {SafeERC20} from "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import {ReentrancyGuard} from "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import {ERC2771Context} from "@openzeppelin/contracts/metatx/ERC2771Context.sol";


/// @notice Interface for Aave V3 Pool flash loan functionality.
/// @dev Only includes the flashLoanSimple function needed for single-asset flash loans.
interface IPool {
    function flashLoanSimple(
        address receiverAddress,
        address asset,
        uint256 amount,
        bytes calldata params,
        uint16 referralCode
    ) external;
}


/// @notice Interface for Uniswap V2 compatible DEX routers.
/// @dev Works with Uniswap V2, Sushiswap, and other compatible forks.
interface IUniswapV2Router02 {
    function swapExactTokensForTokens(
        uint256 amountIn,
        uint256 amountOutMin,
        address[] calldata path,
        address to,
        uint256 deadline
    ) external returns (uint256[] memory amounts);
}


/// @title Gasless Flash Arbitrage Contract
/// @author Flash Arb Team
/// @notice Executes gasless flash loan arbitrage between Uniswap and Sushiswap using Aave flash loans.
/// @dev Uses ERC-2771 meta-transactions via Gelato Network for gas-free execution.
///      Implements Checks-Effects-Interactions pattern for reentrancy safety.
///      All external entry points are protected with ReentrancyGuard.
///      Security features: Timelock for withdrawals and forwarder changes, path validation,
///      asset validation, deadline bounds, and slippage protection.
/// @custom:security-contact security@flasharb.example.com
contract GaslessFlashArb is ReentrancyGuard, ERC2771Context {
    using SafeERC20 for IERC20;


    // ══════════════════════════════════════════════════════════════════════════
    // CONSTANTS
    // ══════════════════════════════════════════════════════════════════════════

    /// @notice Timelock duration for sensitive operations (48 hours).
    uint256 public constant TIMELOCK_DURATION = 48 hours;

    /// @notice Maximum allowed deadline offset from current time (30 minutes).
    uint256 public constant MAX_DEADLINE_OFFSET = 30 minutes;

    /// @notice Minimum acceptable slippage tolerance (95% of input = max 5% slippage).
    uint256 public constant MIN_SLIPPAGE_BPS = 9500;


    // ══════════════════════════════════════════════════════════════════════════
    // STATE VARIABLES
    // ══════════════════════════════════════════════════════════════════════════

    /// @notice The owner of the contract who can execute arbitrage and withdraw funds.
    address public immutable owner;

    /// @notice The Aave V3 lending pool for flash loans.
    IPool public immutable aavePool;

    /// @notice The Uniswap V2 router for the first DEX swap.
    IUniswapV2Router02 public immutable uniRouter;

    /// @notice The Sushiswap V2 router for the second DEX swap.
    IUniswapV2Router02 public immutable sushiRouter;

    /// @notice The WETH token used as the base asset for arbitrage.
    IERC20 public immutable weth;

    /// @notice The USDC token used as the intermediate asset in swaps.
    IERC20 public immutable usdc;

    /// @notice Minimum profit in wei required for an arbitrage to succeed.
    /// @dev Trades below this threshold will revert with InsufficientProfit.
    uint256 public profitThresholdWei;

    /// @notice Maximum percentage of available liquidity to borrow (1-100).
    /// @dev Used by off-chain components to calculate safe borrow amounts.
    uint256 public maxBorrowPct = 5;

    /// @notice Mapping of addresses that are trusted as meta-transaction forwarders.
    /// @dev Multiple forwarders can be trusted for redundancy.
    mapping(address => bool) public trustedForwarderMap;

    /// @notice Pending withdrawal timelock timestamp (0 = no pending withdrawal).
    uint256 public pendingWithdrawalTime;

    /// @notice Pending forwarder additions with their unlock times.
    mapping(address => uint256) public pendingForwarderTime;


    // ══════════════════════════════════════════════════════════════════════════
    // EVENTS
    // ══════════════════════════════════════════════════════════════════════════

    /// @notice Emitted when an arbitrage attempt is initiated.
    event ArbAttempt(address indexed executor, uint256 amount, uint256 minProfit);

    /// @notice Emitted when an arbitrage trade completes successfully.
    event ArbSuccess(address indexed executor, uint256 profit);

    /// @notice Emitted when an arbitrage trade fails before reverting.
    event ArbFail(address indexed executor, string reason);

    /// @notice Emitted when the profit threshold is updated by the owner.
    event ProfitThresholdUpdated(uint256 oldThreshold, uint256 newThreshold);

    /// @notice Emitted when the max borrow percentage is updated by the owner.
    event MaxBorrowPctUpdated(uint256 oldPct, uint256 newPct);

    /// @notice Emitted when a forwarder's trust status is changed.
    event ForwarderUpdated(address indexed forwarder, bool trusted);

    /// @notice Emitted when a forwarder addition is proposed.
    event ForwarderProposed(address indexed forwarder, uint256 unlockTime);

    /// @notice Emitted when a withdrawal is initiated (timelock started).
    event WithdrawalInitiated(uint256 unlockTime);

    /// @notice Emitted when a withdrawal is executed after timelock.
    event WithdrawalExecuted(uint256 wethAmount, uint256 usdcAmount);

    /// @notice Emitted when residual tokens are swept.
    event TokensSwept(address indexed token, uint256 amount);


    // ══════════════════════════════════════════════════════════════════════════
    // ERRORS
    // ══════════════════════════════════════════════════════════════════════════

    /// @notice Thrown when a required address parameter is the zero address.
    error InvalidAddress();

    /// @notice Thrown when a function restricted to owner is called by another address.
    error OwnerOnly();

    /// @notice Thrown when a meta-transaction originates from an untrusted forwarder.
    error UntrustedForwarder();

    /// @notice Thrown when attempting to borrow zero tokens.
    error ZeroAmount();

    /// @notice Thrown when the flash loan callback is called by an address other than Aave.
    error AaveOnly();

    /// @notice Thrown when the flash loan callback's initiator is not this contract.
    error InvalidInitiator();

    /// @notice Thrown when the flash loan asset is not WETH.
    error InvalidAsset();

    /// @notice Thrown when the Uniswap swap output is below the specified minimum.
    error UniSlippageFail();

    /// @notice Thrown when the final profit is below the required minimum threshold.
    error InsufficientProfit();

    /// @notice Thrown when the Uniswap swap returns zero tokens or fails.
    error UniswapSwapFailed();

    /// @notice Thrown when the Sushiswap swap returns zero tokens or fails.
    error SushiswapSwapFailed();

    /// @notice Thrown when swap path is invalid.
    error InvalidPath();

    /// @notice Thrown when deadline is invalid.
    error InvalidDeadline();

    /// @notice Thrown when slippage parameters are too loose.
    error SlippageTooHigh();

    /// @notice Thrown when timelock has not expired.
    error TimelockNotExpired();

    /// @notice Thrown when no pending operation exists.
    error NoPendingOperation();

    /// @notice Thrown when trying to sweep primary tokens.
    error CannotSweepPrimaryTokens();


    // ══════════════════════════════════════════════════════════════════════════
    // MODIFIERS
    // ══════════════════════════════════════════════════════════════════════════

    /// @notice Restricts function access to the contract owner only.
    modifier onlyOwner() {
        if (_msgSender() != owner) revert OwnerOnly();
        _;
    }


    // ══════════════════════════════════════════════════════════════════════════
    // CONSTRUCTOR
    // ══════════════════════════════════════════════════════════════════════════

    /// @notice Initializes the gasless flash arbitrage contract.
    /// @param aavePool_ Address of the Aave V3 Pool contract for flash loans.
    /// @param uniRouter_ Address of the Uniswap V2 Router contract.
    /// @param sushiRouter_ Address of the Sushiswap V2 Router contract.
    /// @param weth_ Address of the WETH token contract.
    /// @param usdc_ Address of the USDC token contract.
    /// @param trustedForwarder_ Address of the initial trusted forwarder (e.g., Gelato Relay).
    constructor(
        address aavePool_,
        address uniRouter_,
        address sushiRouter_,
        address weth_,
        address usdc_,
        address trustedForwarder_
    )
        ERC2771Context(trustedForwarder_)
    {
        if (aavePool_ == address(0)) revert InvalidAddress();
        if (uniRouter_ == address(0)) revert InvalidAddress();
        if (sushiRouter_ == address(0)) revert InvalidAddress();
        if (weth_ == address(0)) revert InvalidAddress();
        if (usdc_ == address(0)) revert InvalidAddress();

        owner = _msgSender();
        aavePool = IPool(aavePool_);
        uniRouter = IUniswapV2Router02(uniRouter_);
        sushiRouter = IUniswapV2Router02(sushiRouter_);
        weth = IERC20(weth_);
        usdc = IERC20(usdc_);

        trustedForwarderMap[trustedForwarder_] = true;
    }


    // ══════════════════════════════════════════════════════════════════════════
    // EXTERNAL FUNCTIONS
    // ══════════════════════════════════════════════════════════════════════════

    /// @notice Initiates a flash loan arbitrage between Uniswap and Sushiswap.
    /// @dev Validates paths, deadline, and slippage parameters before execution.
    /// @param amount The amount of WETH to borrow from Aave (in wei).
    /// @param pathUni The token swap path for Uniswap (must start with WETH, end with USDC).
    /// @param pathSushi The token swap path for Sushiswap (must start with USDC, end with WETH).
    /// @param minOutOnUni Minimum USDC to receive from Uniswap (slippage protection).
    /// @param minOutOnSushi Minimum WETH to receive from Sushiswap (slippage protection).
    /// @param deadline Unix timestamp after which the swaps will revert.
    /// @param minProfitWei Minimum profit in wei required for the arbitrage to succeed.
    function executeArb(
        uint256 amount,
        address[] calldata pathUni,
        address[] calldata pathSushi,
        uint256 minOutOnUni,
        uint256 minOutOnSushi,
        uint256 deadline,
        uint256 minProfitWei
    )
        external
        nonReentrant
    {
        if (!isTrustedForwarder(msg.sender)) revert UntrustedForwarder();
        if (_msgSender() != owner) revert OwnerOnly();
        if (amount == 0) revert ZeroAmount();

        // Validate paths (HIGH: No Validation of DEX Router Path Arrays)
        if (pathUni.length < 2) revert InvalidPath();
        if (pathSushi.length < 2) revert InvalidPath();
        if (pathUni[0] != address(weth)) revert InvalidPath();
        if (pathUni[pathUni.length - 1] != address(usdc)) revert InvalidPath();
        if (pathSushi[0] != address(usdc)) revert InvalidPath();
        if (pathSushi[pathSushi.length - 1] != address(weth)) revert InvalidPath();

        // Validate deadline (LOW: Deadline Parameter Not Validated)
        if (deadline < block.timestamp) revert InvalidDeadline();
        if (deadline > block.timestamp + MAX_DEADLINE_OFFSET) revert InvalidDeadline();

        // Validate slippage is reasonable (MEDIUM: Missing Slippage Parameter Validation)
        // minOutOnUni should be at least 95% of expected output
        // We can't calculate exact expected here, but we ensure they're not zero
        if (minOutOnUni == 0) revert SlippageTooHigh();
        if (minOutOnSushi == 0) revert SlippageTooHigh();

        emit ArbAttempt(_msgSender(), amount, minProfitWei);

        bytes memory data = abi.encode(
            amount,
            pathUni,
            pathSushi,
            minOutOnUni,
            minOutOnSushi,
            deadline,
            minProfitWei
        );
        aavePool.flashLoanSimple(address(this), address(weth), amount, data, 0);
    }

    /// @notice Callback function invoked by Aave after granting the flash loan.
    /// @dev Validates asset matches WETH and enforces profitThresholdWei.
    /// @param asset The asset that was borrowed (must be WETH).
    /// @param amount The amount of WETH that was borrowed.
    /// @param premium The fee charged by Aave for the flash loan.
    /// @param initiator The address that initiated the flash loan (must be this contract).
    /// @param params ABI-encoded arbitrage parameters from executeArb.
    /// @return success True if the arbitrage and repayment completed successfully.
    function executeOperation(
        address asset,
        uint256 amount,
        uint256 premium,
        address initiator,
        bytes calldata params
    )
        external
        returns (bool success)
    {
        // Checks
        if (msg.sender != address(aavePool)) revert AaveOnly();
        if (initiator != address(this)) revert InvalidInitiator();
        // HIGH: Missing Asset Validation in Flash Loan Callback
        if (asset != address(weth)) revert InvalidAsset();

        // Decode parameters
        (
            uint256 borrowAmount,
            address[] memory pathUni,
            address[] memory pathSushi,
            uint256 minOutOnUni,
            uint256 minOutOnSushi,
            uint256 deadline,
            uint256 minProfitWei
        ) = abi.decode(
            params,
            (uint256, address[], address[], uint256, uint256, uint256, uint256)
        );

        // Track initial USDC balance for accurate profit calculation
        uint256 usdcBalanceBefore = usdc.balanceOf(address(this));

        // First swap: WETH -> USDC on Uniswap
        weth.forceApprove(address(uniRouter), borrowAmount);

        uint256[] memory amounts = uniRouter.swapExactTokensForTokens(
            borrowAmount,
            minOutOnUni,
            pathUni,
            address(this),
            deadline
        );

        if (amounts.length == 0 || amounts[amounts.length - 1] == 0) {
            revert UniswapSwapFailed();
        }

        // Verify slippage protection
        uint256 receivedUSDC = usdc.balanceOf(address(this)) - usdcBalanceBefore;
        if (receivedUSDC < minOutOnUni) revert UniSlippageFail();

        // Second swap: USDC -> WETH on Sushiswap
        usdc.forceApprove(address(sushiRouter), receivedUSDC);

        amounts = sushiRouter.swapExactTokensForTokens(
            receivedUSDC,
            minOutOnSushi,
            pathSushi,
            address(this),
            deadline
        );

        if (amounts.length == 0 || amounts[amounts.length - 1] == 0) {
            revert SushiswapSwapFailed();
        }

        // Calculate profit and verify minimum threshold
        uint256 finalWETH = weth.balanceOf(address(this));
        uint256 repayAmount = amount + premium;

        // LOW: Use maximum of minProfitWei and profitThresholdWei
        uint256 effectiveMinProfit = minProfitWei > profitThresholdWei 
            ? minProfitWei 
            : profitThresholdWei;

        if (finalWETH < repayAmount + effectiveMinProfit) {
            emit ArbFail(owner, "Insufficient profit after swaps");
            revert InsufficientProfit();
        }

        // Approve repayment to Aave
        weth.forceApprove(address(aavePool), repayAmount);

        // Emit success event with profit amount
        uint256 profit = finalWETH - repayAmount;
        emit ArbSuccess(owner, profit);

        return true;
    }

    /// @notice Initiates withdrawal with timelock protection.
    /// @dev HIGH: Centralized Owner Control - adds 48 hour delay for withdrawals.
    function initiateWithdrawal() external onlyOwner {
        pendingWithdrawalTime = block.timestamp + TIMELOCK_DURATION;
        emit WithdrawalInitiated(pendingWithdrawalTime);
    }

    /// @notice Executes withdrawal after timelock expires.
    function executeWithdrawal() external onlyOwner {
        if (pendingWithdrawalTime == 0) revert NoPendingOperation();
        if (block.timestamp < pendingWithdrawalTime) revert TimelockNotExpired();

        pendingWithdrawalTime = 0;

        uint256 wethBalance = weth.balanceOf(address(this));
        if (wethBalance > 0) {
            weth.safeTransfer(owner, wethBalance);
        }

        uint256 usdcBalance = usdc.balanceOf(address(this));
        if (usdcBalance > 0) {
            usdc.safeTransfer(owner, usdcBalance);
        }

        emit WithdrawalExecuted(wethBalance, usdcBalance);
    }

    /// @notice Cancels a pending withdrawal.
    function cancelWithdrawal() external onlyOwner {
        if (pendingWithdrawalTime == 0) revert NoPendingOperation();
        pendingWithdrawalTime = 0;
    }

    /// @notice Proposes adding a new trusted forwarder with timelock.
    /// @dev HIGH: Unrestricted Trusted Forwarder Addition - adds 48 hour delay.
    function proposeForwarder(address forwarder) external onlyOwner {
        if (forwarder == address(0)) revert InvalidAddress();
        pendingForwarderTime[forwarder] = block.timestamp + TIMELOCK_DURATION;
        emit ForwarderProposed(forwarder, pendingForwarderTime[forwarder]);
    }

    /// @notice Confirms a forwarder addition after timelock expires.
    function confirmForwarder(address forwarder) external onlyOwner {
        if (pendingForwarderTime[forwarder] == 0) revert NoPendingOperation();
        if (block.timestamp < pendingForwarderTime[forwarder]) revert TimelockNotExpired();

        delete pendingForwarderTime[forwarder];
        trustedForwarderMap[forwarder] = true;
        emit ForwarderUpdated(forwarder, true);
    }

    /// @notice Revokes trust from a forwarder immediately (emergency action).
    function revokeForwarder(address forwarder) external onlyOwner {
        trustedForwarderMap[forwarder] = false;
        delete pendingForwarderTime[forwarder];
        emit ForwarderUpdated(forwarder, false);
    }

    /// @notice Sweeps residual tokens (excluding WETH and USDC).
    /// @dev LOW: No Protection Against Residual Token Accumulation.
    function sweepTokens(address token) external onlyOwner {
        if (token == address(weth) || token == address(usdc)) {
            revert CannotSweepPrimaryTokens();
        }
        uint256 balance = IERC20(token).balanceOf(address(this));
        if (balance > 0) {
            IERC20(token).safeTransfer(owner, balance);
            emit TokensSwept(token, balance);
        }
    }

    /// @notice Sets the minimum profit threshold for arbitrage trades.
    function setProfitThreshold(uint256 threshold) external onlyOwner {
        uint256 oldThreshold = profitThresholdWei;
        profitThresholdWei = threshold;
        emit ProfitThresholdUpdated(oldThreshold, threshold);
    }

    /// @notice Sets the maximum percentage of liquidity to borrow.
    function setMaxBorrowPct(uint256 pct) external onlyOwner {
        uint256 oldPct = maxBorrowPct;
        maxBorrowPct = pct;
        emit MaxBorrowPctUpdated(oldPct, pct);
    }


    // ══════════════════════════════════════════════════════════════════════════
    // PUBLIC VIEW FUNCTIONS
    // ══════════════════════════════════════════════════════════════════════════

    /// @notice Checks if an address is a trusted meta-transaction forwarder.
    function isTrustedForwarder(address forwarder) public view override returns (bool trusted) {
        return trustedForwarderMap[forwarder];
    }


    // ══════════════════════════════════════════════════════════════════════════
    // INTERNAL FUNCTIONS
    // ══════════════════════════════════════════════════════════════════════════

    /// @notice Returns the sender of the transaction, accounting for meta-transactions.
    function _msgSender() internal view override returns (address sender) {
        return ERC2771Context._msgSender();
    }

    /// @notice Returns the calldata of the transaction, accounting for meta-transactions.
    function _msgData() internal view override returns (bytes calldata data) {
        return ERC2771Context._msgData();
    }
}

