// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract MockAavePool {
    uint256 public constant PREMIUM_BPS = 9; // 0.09% like real Aave

    event FlashLoan(address receiver, address asset, uint256 amount, bytes params);

    function flashLoanSimple(
        address receiverAddress,
        address asset,
        uint256 amount,
        bytes calldata params,
        uint16
    ) external {
        uint256 premium = (amount * PREMIUM_BPS) / 10000;

        IERC20(asset).transfer(receiverAddress, amount);

        (bool ok, bytes memory returnData) = receiverAddress.call(
            abi.encodeWithSignature(
                "executeOperation(address,uint256,uint256,address,bytes)",
                asset,
                amount,
                premium,
                msg.sender,
                params
            )
        );
        if (!ok) {
            // Propagate the original error
            if (returnData.length > 0) {
                assembly {
                    revert(add(returnData, 32), mload(returnData))
                }
            }
            revert("callback failed");
        }

        IERC20(asset).transferFrom(receiverAddress, address(this), amount + premium);

        emit FlashLoan(receiverAddress, asset, amount, params);
    }

    function depositLiquidity(address token, uint256 amount) external {
        IERC20(token).transferFrom(msg.sender, address(this), amount);
    }
}

