// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import {IERC20} from "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract MockRouter {
    uint256 public exchangeRateNumerator = 100;
    uint256 public exchangeRateDenominator = 100;

    // Mock function to control the amountOut for testing
    function setMockAmountOut(uint256 expectedAmountIn, uint256 expectedAmountOut) external {
        if (expectedAmountIn == 0) {
            exchangeRateNumerator = 0;
            exchangeRateDenominator = 1;
        } else {
            exchangeRateNumerator = expectedAmountOut;
            exchangeRateDenominator = expectedAmountIn;
        }
    }

    function setExchangeRate(uint256 numerator, uint256 denominator) external {
        exchangeRateNumerator = numerator;
        exchangeRateDenominator = denominator;
    }

    function swapExactTokensForTokens(
        uint amountIn,
        uint amountOutMin,
        address[] calldata path,
        address to,
        uint deadline
    ) external returns (uint[] memory amounts) {
        require(path.length >= 2, "Invalid path");
        require(block.timestamp <= deadline, "Expired");

        address tokenIn = path[0];
        address tokenOut = path[path.length - 1];

        IERC20(tokenIn).transferFrom(msg.sender, address(this), amountIn);

        uint256 amountOut = (amountIn * exchangeRateNumerator) / exchangeRateDenominator;
        require(amountOut >= amountOutMin, "Slippage");

        IERC20(tokenOut).transfer(to, amountOut);

        amounts = new uint[](path.length);
        amounts[0] = amountIn;
        amounts[path.length - 1] = amountOut;
    }
}

